Vue.component("colorPicker",{props:["value"],template:'<input type="text">',mounted(){const e=this;$(this.$el).val(this.value).wpColorPicker({width:200,defaultColor:this.value,change(t,o){e.$emit("input",o.color.toString())}})},watch:{value(e){$(this.$el).wpColorPicker("color",e)}},destroyed(){$(this.$el).off().wpColorPicker("destroy")}});